from libpython_01.spam.modelos import Usuariodef test_salvar_usuario(sessao):    usuario = Usuario(nome='andre',email='andresousadb@gmail.com')    sessao.salvar(usuario)    assert isinstance(usuario.id, int)def test_listar_usuarios(sessao):    usuarios = [        Usuario(nome='andre',email='andresousadb@gmail.com'),        Usuario(nome='ferreira',email='andresousadb@gmail.com')    ]    for usuario in usuarios:        sessao.salvar(usuario)    assert usuarios == sessao.listar()